mapper.update:
update unit_info
set unit_name = #{unitName,jdbcType=VARCHAR},
  unit_type_id = #{unitTypeId,jdbcType=INTEGER},
  unit_sort_num = #{unitSortNum,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, unit_name, unit_type_id, unit_sort_num
from unit_info
where id = #{id,jdbcType=INTEGER}
