mapper.select:
select id, user_name, user_password, user_operator_id, user_operator_time, user_type, user_area_id,user_del_state from sys_user where user_type = 2
and user_name like #{userName,jdbcType=VARCHAR}
and user_area_id = #{schoolId,jdbcType=INTEGER}
and user_area_id in (select id from sys_area where area_del_state = 0 and area_parent_id = #{cityId} )
and user_area_id in (select id from sys_area where area_del_state = 0 and area_parent_id in (select id from sys_area where area_del_state = 0 and area_parent_id =#{provinceId}) )
and user_del_state = 0 order by user_area_id,id

mapper.update:
update sys_user
set user_del_state = 1
where id = #{id,jdbcType=INTEGER}

update sys_user
set user_name = #{userName,jdbcType=VARCHAR},
  user_operator_id = #{userOperatorId,jdbcType=INTEGER},
  user_operator_time = #{userOperatorTime,jdbcType=TIMESTAMP},
  user_area_id = #{userAreaId,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

update sys_user
set user_password = #{pwd,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}
